package Relacion1;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=144;
		int y=999;
		System.out.println("El valor de la primera variable es "+x);
		System.out.println("El valor de la segunda variable es "+y);
		int suma=x+y;
		int resta1=x-y;
		int resta2=y-x;
		int multiplicacion=x*y;
		int division1=x/y;
		int division2=y/x;
		System.out.println("La suma de las dos variables es "+suma);
		System.out.println("La resta de la 1ª variable menos la 2ª es "+resta1);
		System.out.println("La resta de la 2ª variable menos la 1ª es "+resta2);
		System.out.println("La multiplicación de las dos variables es "+multiplicacion);
		System.out.println("La división de la 1ª variable entre la 2ª es "+division1);
		System.out.println("La división de la 2ª variable entre la 1ª es "+division2);
	}

}
