package Relacion2;

public class Ejercicio_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Creo un programa que muestre los números "
				+ " los múltiplos de 5 de 0 a 100");
		int num=0;
		do{
			System.out.println(num);
			num=num+5;
		}while (num<=100);
	}

}
